package com.practice.Dec;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.RequestMapping;@Controller@RequestMappingpublic class connectTestController {    @Autowired    private connectTest connectTest;    @GetMapping(value = "/")    public String connectTestController(Model model){        String sysdate = connectTest.selectCurTime();        System.out.println("[sysdate 값] : " + sysdate);        model.addAttribute("date", sysdate );        System.out.println("[model 결과] : " + model);        return "connectTest";    }}